$(function(){	'use strict';		// Globals	var doc = $(document);	var body = $('body');	var win = $(window);			// Load Scripts Async	var script_arr = [];	script_arr.push(		'assets/js/libs/bootstrap/transition.js',		//'assets/js/libs/bootstrap/alert.js',		//'assets/js/libs/bootstrap/button.js',		//'assets/js/libs/bootstrap/carousel.js',		//'assets/js/libs/bootstrap/collapse.js',		'assets/js/libs/bootstrap/dropdown.js',		//'assets/js/libs/bootstrap/modal.js',		'assets/js/libs/bootstrap/tab.js',		'assets/js/libs/dotdotdot/jquery.dotdotdot.min.js',		//'assets/js/libs/bootstrap/affix.js',		//'assets/js/libs/bootstrap/scrollspy.js',		//'assets/js/libs/bootstrap/tooltip.js',		//'assets/js/libs/bootstrap/popover.js'		'assets/js/applify/components.js',		'assets/js/applify/modules.js'	);	$.getMultiScripts = function(arr, path) {		var _arr = $.map(arr, function(scr) {return $.getScript( (path||"") + scr );});		_arr.push($.Deferred(function( deferred ){$( deferred.resolve );}));		return $.when.apply($, _arr);	};	// Load The Scripts				$.getMultiScripts(script_arr, '').done(function() {					// Navbar		$('.navbar').ui_navbar();		// UltraHD Images @2x		if($('[data-uhd]').length){$('[data-uhd]').ui_uhd_images();}		// UI Scroll to Sections		if($('[data-scrollto]').length){$('[data-scrollto]').ui_scroll_to();}		// UI Hero Slider		if($('.ui-hero-slider').length){$('.ui-hero-slider').ui_hero_slider();}		// UI App Showcase		if($('.ui-app-showcase').length){$('.ui-app-showcase').ui_app_showcase();}		// UI Accordion		if($('.ui-accordion-panel').length){$('.ui-accordion-panel').ui_accordion();}		// UI Cover Images		if($('.ui-cover-img').length){$('.ui-cover-img').ui_cover_image();}		// UI Device Slider		if($('.ui-device-slider').length){			$('.ui-device-slider .screens').each(function(){				$(this).ui_device_slider();			});		}		// UI Pricing Cards		if($('.ui-pricing-cards').length){$('.ui-pricing-cards').ui_pricing_cards();}		// UI Testimonials 		if ($('.ui-testimonials').length) {$('.ui-testimonials').ui_testimonials();}		// UI Stats 		if ($('.ui-stats').length) {$('.ui-stats').ui_stats();}		// UI App Screens		if ($('.ui-app-screens').length) {$('.ui-app-screens').ui_app_screens();}		// Video Player		if($('.ui-video-toggle').length){$('.ui-video-toggle').ui_video_player();}		// Collapsible Nav		if($('.ui-collapsible-nav').length){doc.ui_collapsible_nav();}		// UI Action Card 		if ($('.ui-action-card').length) {$('.ui-action-card').ui_action_card();}		//Instagram Feed		if ($('.ui-instagram-widget .insta-feed').length) { $('.ui-instagram-widget .insta-feed').ui_instagram_feed();}				// Window Resize		win.on('resize', function(){			// UI Turncate Text			if($('.ui-turncate-text').length){				var content = $('.ui-turncate-text').triggerHandler("originalContent");				$('.ui-turncate-text p').html( content );				$('.ui-turncate-text').dotdotdot();			}			$('[data-vertical_center="true"]').each(function(){				var e = $(this);				var p = e.parent();				var o = e.data('vertical_offset');				var y = 0;				if(o){					y = ((p.height() - e.height()) / 2) - o;				}				else{					y = (p.height() - e.height()) / 2;				}				var px = p.width();				var ex = e.outerWidth();				if(ex < px){					e.css({'margin-top': y+'px'});				}				else{					e.css({'margin-top': '0'});				}			});		});				// Trigger Methods		doc.trigger('jsDidLoad');		win.trigger('scroll');		win.trigger('resize');	});	// Images Did Load	doc.on('images_did_load', function(){		win.trigger('resize');		setTimeout(function(){			$('[data-fade_in="on-load"]').css({				'opacity': '1'			});		},250);	});	// All Javascript Did Loaded	doc.on('jsDidLoad', function(){		/* { .. custom js here ..  } */	});	});