(function($) {	"use strict";	var doc = $(document);	var win = $(window);	var body = $('body');	var breaks = {		xs:480,		sm:740,        md:992,		lg:1200	};		// UI Navbar	$.fn.ui_navbar = function (){		var navbar = this;		var toggle = $('.ui-mobile-nav-toggle');		var navbar_nav = $('.ui-navigation');		// Scroll Function		win.scroll(function() {			var scroll_top = $(this).scrollTop();			if (body.hasClass('ui-transparent-nav') && !body.hasClass('mobile-nav-active')) {				if (scroll_top >= 24) {					navbar.removeClass('transparent');				} else {					navbar.addClass('transparent');				}			}		});		// Nav Toggle HTML		toggle.html('<div><span></span><span></span><span></span><span></span></div>');		// Nav Toggle Action		var toggle_nav = function(){			var win_top = win.scrollTop();			// Nav Is NOT Active			if (!body.hasClass('mobile-nav-active')) {				body.addClass('mobile-nav-active');				toggle.addClass('active');				navbar_nav.slideDown(250);				if (body.hasClass('ui-transparent-nav')) {					navbar.removeClass('transparent');				}			}			// Nav IS Active			else{				body.removeClass('mobile-nav-active');				toggle.removeClass('active');				navbar_nav.slideUp(200);				if (body.hasClass('ui-transparent-nav')) {					if (win_top < 24) {						navbar.addClass('transparent');					}				}			}		};		toggle.on('click', function(e){			e.preventDefault();			toggle_nav();		});		win.resize(function() {			var w = $(this).width();			var win_top = win.scrollTop();			if(w >= 740){				if (body.hasClass('mobile-nav-active')) {					body.removeClass('mobile-nav-active');					toggle.removeClass('active');					if (body.hasClass('ui-transparent-nav')) {						if (win_top < 24) {							navbar.addClass('transparent');						}					}				}				navbar_nav.show();			}			else{				if (!body.hasClass('mobile-nav-active')) {					navbar_nav.hide();				}			}		});	};		// UI Hero Slider	$.fn.ui_hero_slider = function(){		var ui_slider = this;		var slider_height = ui_slider.find('.container').outerHeight();		ui_slider.find('.sp-slides').css({'height': slider_height+'px'});		// Settings		var fade_enabled = ui_slider.data('fade');		var touch_enabled = ui_slider.data('touch_swipe');		var autoplay_enabled = ui_slider.data('auto_play');		var autoplay_delay = ui_slider.data('autoplay_delay');		var autoplay_on_hover = ui_slider.data('autoplay_on_hover');		var show_dots = ui_slider.data('show_dots');		var show_arrows = ui_slider.data('show_arrows');		// All Javascript Did Loaded		ui_slider.sliderPro({			width: '100%',			height: '100%',			autoHeight: true,			fade: fade_enabled,			touchSwipe: touch_enabled,			arrows: show_arrows,			buttons: show_dots,			autoplay: autoplay_enabled,			autoplayDelay: autoplay_delay,			autoplayOnHover: autoplay_on_hover,			waitForLayers: true,			fadeOutPreviousSlide: true,			fadeDuration: 1000,			autoScaleLayers: false,			init: function(){				setTimeout(function(){					ui_slider.find('.sp-slides').removeClass('fade');				},1000);			}		});		win.on('focus', function(){			ui_slider.sliderPro( 'nextSlide' );		});	};		// UI App Showcase	$.fn.ui_app_showcase = function(){		var showcase = this;		var col_txt_a = this.find('[data-col="text_a"]').get(0).outerHTML;		var col_txt_b = this.find('[data-col="text_b"]').get(0).outerHTML;		var col_img = this.find('[data-col="img"]').get(0).outerHTML;				win.on('resize', function(){			if($(this).width() >= breaks.sm){				showcase.html(col_txt_a + col_img + col_txt_b);			}			else{				showcase.html(col_img + '<div class="col-xs-8" data-vertical_center="true"><div class="row">' + col_txt_a + col_txt_b + '</div></div>');			}		});				};		// UI App Screens	$.fn.ui_app_screens = function(){		this.owlCarousel({			center: true,			loop:true,			margin:16,			autoWidth:true		});	};		// UI Device Slider	$.fn.ui_device_slider = function () {		var id = this.attr('id');		var items = this.find('.item').length;		var z = false;		var y = false;		var owl = this.owlCarousel({			loop:true,			margin:0,			nav:false,			dots: true,			items:1,			autoWidth:true		});		owl.on('changed.owl.carousel', function(event) {			z = true;			if(!y){					var slide = event.relatedTarget.normalize(event.item.index, true) -2; 			   	if (slide < 0){					slide = items + slide + 1;				}				else{					slide = slide + 1;				}				console.log(slide);				$('[data-toggle_slider="'+id+'"][data-toggle_screen="'+slide+'"]').trigger('click');			}			z = false;		});		$('[data-toggle_slider="'+id+'"][data-toggle_screen]').on('click', function(){			y = true;			if(!z){				var i = $(this).data('toggle_screen') - 1;				owl.trigger('to.owl.carousel', i);			}			y = false;		});	};		// UI Pricing Cards	$.fn.ui_pricing_cards = function(){		var cards = this;		cards.owlCarousel({			loop:false,			margin:0,			nav:true,			//autoWidth:true,			responsive:{				0:{					items:1				},				740:{					items:3				}			}		});		function to_active_card(){ 			cards.trigger('to.owl.carousel', 1);		}		var z;		win.on('resize', function(){		  	clearTimeout(z);		  	z = setTimeout(to_active_card, 500);		});	};		// UI Testimonials	$.fn.ui_testimonials = function(){		this.owlCarousel({			loop:true,			margin:16,			nav:true,			responsive:{				0:{					items:1				},				740:{					items:2				},				992:{					items:3				}			}		});	};		// UI Stats	$.fn.ui_stats = function(){		var stats = this;		var stat = stats.find('.stat');		var duration = stats.data('duration');		var counted = false;		win.scroll(function(){			if (stats.isOnScreen()) {				if(!counted){					count();				}				counted = true;			}		});		function count(){			stat.each(function () {				$(this).prop('Counter',0).animate({					Counter: $(this).text()				}, {					duration: duration,					easing: 'swing',					step: function (now) {						$(this).text(Math.ceil(now));					}				});			});		}			};	}(jQuery));